
public class CastingExTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
	
		double doubleNum = 12.123;
		int n = 0; 
	    
		n = (int)doubleNum;
		
		System.out.println(n);
	    
		doubleNum = (double)n;
		doubleNum = n;
		 
		System.out.println(doubleNum);
		
				
				
				
				

	}

}
